    final private int rows = mapArray.length;
    final private int columns = mapArray[0].length;
    Window window;

    private ArrayList<Tile> tileList = new ArrayList<>();

    public TraficMap(Window window) {
        this.window = window;
        generateTileList();

        // Adds cars on random roads
        for (Tile tile : tileList) {
            if (tile instanceof Road) {
                if (Math.random() < 0.05)
                ((Road) tile).addCar(new Car(this.window, (Road) tile));


            }
        }
    }

    // Creates new tiles and adds them into the tileArray depending on content of mapArray at given index
    public void generateTileList() {
        for (int y = 0; y < mapArray.length; y++) {
            for (int x = 0; x < mapArray[0].length; x++) {
                if (mapArray[y][x] == 0) tileList.add(new Terrain(y, x));
                if (mapArray[y][x] == 1) tileList.add(new Road(y, x, "up", this));
                if (mapArray[y][x] == 2) tileList.add(new Road(y, x, "right", this));
                if (mapArray[y][x] == 3) tileList.add(new Road(y, x, "down", this));
                if (mapArray[y][x] == 4) tileList.add(new Road(y, x, "left", this));
                if (mapArray[y][x] == 5) tileList.add(new Terrain(y, x));
                if (mapArray[y][x] == 6) tileList.add(new Parkingspace(y, x));
            }
        }

        //Array that puts the intersections in place, they need to check all roads
        for (int y = 0; y < mapArray.length; y++) {
            for (int x = 0; x < mapArray[0].length; x++) {
                if (mapArray[y][x] == 5)
                    this.tileList.set(this.tileList.indexOf(getTileFromPosition(y, x)), new Intersection(y, x, this));
            }
        }
    }

    public Tile getTileFromPosition(int yPosition, int xPosition) {
        System.out.println("c: " + columns);
        System.out.println("r: " + rows);

        return this.tileList.get((this.columns * yPosition) + xPosition);
    }


    public void addTilesToWindow(Window window) {
        for (Tile tile : this.tileList) {
            window.add(tile);
        }
    }

    public ArrayList<Tile> getTileList() {
        return this.tileList;
    }

    public int getColumns() {
        return columns;
    }

    public int getRows() {
        return rows;
    }
